import React, { useState } from "react";
import API from "../API";
import "../styles/Auth.css";

export default function RegisterPage({ onRegister }) {
  const [email, setEmail] = useState("");
  const [pwd, setPwd] = useState("");
  const [confirmPwd, setConfirmPwd] = useState("");
  const [message, setMessage] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");

  // live email validation
  const validateEmail = (value) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!regex.test(value)) setEmailError("Invalid email format!");
    else setEmailError("");
    setEmail(value);
  };

  // live password validation
  const validatePassword = (value) => {
    const rules = /^(?=.*[A-Z])(?=.*[a-z])(?=.*\d)(?=.*[@$!%*?&]).{8,}$/;
    if (!rules.test(value))
      setPasswordError("Password must be 8+ chars, include upper, lower, number, and symbol.");
    else setPasswordError("");
    setPwd(value);
  };

  const handleRegister = async () => {
    if (pwd !== confirmPwd) {
      setMessage("Passwords do not match!");
      return;
    }
    if (emailError || passwordError) {
      setMessage("Please fix errors before submitting!");
      return;
    }

    try {
      await API.post("/auth/register", { email, pwd });
      setMessage("Registration successful! You can now log in.");
      onRegister && onRegister();
    } catch (error) {
      console.error("Registration failed:", error);
      setMessage("Registration failed. Try again.");
    }
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => validateEmail(e.target.value)}
      />
      {emailError && <p className="error-text">{emailError}</p>}

      <input
        type="password"
        placeholder="Password"
        value={pwd}
        onChange={(e) => validatePassword(e.target.value)}
      />
      {passwordError && <p className="error-text">{passwordError}</p>}

      <input
        type="password"
        placeholder="Confirm Password"
        value={confirmPwd}
        onChange={(e) => setConfirmPwd(e.target.value)}
      />

      <button onClick={handleRegister}>Register</button>
      {message && <p className="info-text">{message}</p>}
    </div>
  );
}
