import React, { useState , useEffect } from "react";

function LoginPage() {
  const [cnt,setCnt]=useState(0);
  const [cnt1,setCnt1]=useState(0);
  const orgTitle = "Netflix AI Chatbot";
  useEffect(() => {
    const hvc=()=>{
      if (document.visibilityState === "hidden") {
        setCnt((prev) =>prev+1);
        document.title="Comeback!!!";
      } else {
        document.title=orgTitle;
      }
    };
    document.addEventListener("visibilitychange",hvc);
    return ()=>{
      document.removeEventListener("visibilitychange",hvc);
    };
  },[]);
  const handelReset = () => {
    setCnt(0);
    setCnt1(0);
  }
  return (
    <div>
      <p>Count : {cnt1} </p>
      <p>Tab Switch Count : {cnt}</p>
      <button className="btn" onClick={()=>setCnt1(cnt1+1)}>
        Click ME 
      </button>      
      <button className="btn" onClick={handelReset}>
        Reset
      </button>
      <button className="btn" onClick={()=>setCnt1(cnt1-1)} disabled={cnt1===0}>
        Decrement 
      </button>
    </div>
  );
}

export default LoginPage;
