import React, { useState , useEffect } from "react";

function LoginPage() {
  const [cnt,setCnt]=useState(0);
  const orgTitle = "Netflix AI Chatbot";
  useEffect(() => {
    const hvc=()=>{
      if (document.visibilityState === "hidden") {
        setCnt((prev) =>prev+1);
        document.title="Comeback!!!";
      } else {
        document.title=orgTitle;
      }
    };
    document.addEventListener("visibilitychange",hvc);
    return ()=>{
      document.removeEventListener("visibilitychange",hvc);
    };
  },[cnt]);
  return (
    <div>
      <p>Count : {cnt} </p>
      <button className="btn" onClick={()=>setCnt(cnt+1)}>
        Click ME 
      </button>      
      <button className="btn" onClick={()=>setCnt(0)}>
        Reset
      </button>
      <button className="btn" onClick={()=>setCnt(cnt-1)} disabled={cnt===0}>
        Decrement 
      </button>
    </div>
  );
}

export default LoginPage;
